const { Box, Typography, useTheme, useMediaQuery } = require("@mui/material");

const Login = () => {
    const theme = useTheme();
    const isNonMobileSreen = useMediaQuery("(min-width: 1000px)")

  return (
    <Box>
      <Box
        width="100%"
        backgroundColor={theme.palette.background.alt}
        p="1rem 6%"
        textAlign="center"
      >
        <Typography fontWeight="bold" variant="h3" color="primary">
          SOCIAL MEDIA
        </Typography>
      </Box>

      <Box width={isNonMobileSreen ? "50%": ""}>
        <Typography>Welcome to Socail Media</Typography>

        <div></div>
      </Box>
    </Box>
  );
};

export default Login;
